import React, { Component } from "react";
import Calculator from "./Calculator";
import ProductList from "../Product/ProductList";

class Monitor extends Component {

    constructor(props) {
        super(props);
        this.state = {
            totalPrice: 0, orders: []};
        }

        addOrder(product){
            let findOrder = this.state.orders.find(order=>order.product.productId==product.productId);
            if (findOrder){
                findOrder.quantity++;
            }
            else {
                this.state.orders.push({product: product, quantity :1});
            }
            const totalPrice = this.state.totalPrice + parseInt(product.unitPrice);
            this.setState({totalPrice: totalPrice, order: this.stats.orders}); 
        }

        render() {
            return (
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-9">
                            <ProductList products={this.props.products} />
                        </div>
                        <div className="col-md-3">
                            <Calculator totalPrice={this.state.totalPrice} orders={this.state.orders} />
                        </div>
                    </div>
                </div>
            )

        }

    }

export default Monitor;
