import React, { Component } from "react";
import { productCreate, productUpdate, productFetch } from "../../actions";
import Header from "../../components/Header";
import ProductFrom from "../../components/Product/ProductFrom"
import Footer from "../../components/Footer";


class ProductEdit extends Component {

    componentDidMount() {
        if (this.props.match.params.id)
            this.props.productFetch(this.props.match.params.id);
    }

    render() {
        const { match } = this.props;
        return (<div>
            <Header />
            <div className="container col-md-5">

                {match.path.indexOf("add") > 0 && (
                    <div>
                        <h2>เพิ่ม</h2>
                        <ProductFrom />
                    </div>
                )}
                {match.path.indexOf("edit") > 0 && (
                    <div>
                        <h2>แก้ไข</h2>
                        <ProductFrom />
                    </div>
                )}



                <ProductFrom />
            </div>
            <Footer />

        </div>)

    }
}

function mapStateToProps ({form, products}){
    return{formValues: form.productFrom ? form.productFrom.values : null, products};
}


export default connect(mapStateToProps, { productCreate, productFetch, productUpdate })(ProductEdit);
