import React,{Component} from "react";
import {reduxForm, Field} from "redux-form";
import FormField from "../common/FormField";


class ProductForm extends Component {

    renderFields(){
        const formFields = [
            {label:"Product Name", name:"productName", type:"text"},
            {label:"Unit Price",name:"unitPrice", type:"number"},
            {label:"Thumbnail", name:"thumbnail", type:"text"}
        ];
        return formFields.map(({label, name, type})=>{
            return(
                <Field name={name} type={type} component={FormField} label={label}/>
            )
        })
    }

    render(){
        return(
            <div>
                <from>
                    {this.renderFields()}
                    <button className="btn btn-block btn-info title" type="submit">
                    บันทึก
                </button>
                </from>
            </div>
        )
    }
}

ProductForm = reduxForm({form:"productForm"})(ProductForm);

export default ProductForm;
