import React, { Component } from "react";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import axios from "axios";

class Order extends Component {

    constructor(props) {
        super(props);
        this.state = { orders: null };
    }
    componentDidMount() {
        axios.get("http://localhost:3001/orders").then(
            res => {
                this.setState({ orders: res.data });
            }
        )
    }
    delOrder(order) {
        axios.get("http://localhost:3001/orders" + order.id).then(res => {
            axios.get("http://localhost:3001/orders").then(
                res => {
                    this.setState({ orders: res.data });
                }
            )
        })
    }

    showOrders() {
        return this.state.orders && this.state.orders.map(order => {
            const date = new Date(order.orderedDate);
            return (
                <div key={order.id} className="col-md-3">
                    <hr />
                    <p className="text-right">
                        <button className="btn btn-danger btn-sm title" onClick={() => this.delOrder(order)}>x</button>
                    </p>
                    <h5>วันที่ {date.toLocaleDataString()} {date.toLocaleTimeString()}</h5>
                    <ul>
                        {order.orders && order.orders.map(record => {
                            return (
                                <li key={order.product.id}>
                                    {record.product.productName} x {record.quantity} ={record.product.unitPrice * record.quantity}
                                </li>
                            )
                        })}
                    </ul>
                    <p className="title">ยอดรวม {order.totalPrice} </p>
                </div>
            )
        })
    }

        render() {
            return (
                <div>
                    <Header />
                        <div className="container-fluid">
                            <h1>รายการสั่งซื้อ</h1>
                                <div className="row">
                                    {this.showOrders()}
                                </div>
                        </div>

                    <Footer />
                </div >
            )
        }
    }



    export default Order;
