import React, { Component } from "react";

class ProductItem extends Component {

    constructor(props) {
        super(props);
    }
    doSomething(productName){
       console.log (productName);
    }

    render() {
        const { productName, unitPrice, thumbnail } = this.props;
        return (

            <div className="col-md-3 col-sm-6">
                <img className="img-fluid img-thumbnail" scr={thumbnail} />
                <h5 className="mt-2">{productName}</h5>
                <p className="text-right title">{unitPrice} THB</p>
                <button className="btn btn-block btn-secondary title" onClick={this.doSomething(productName)}>
                    ซื้อ
                </button>
                <hr />
            </div>

        )
    }
}

export default ProductItem;
